        inserts.add("INSERT INTO Relationenschema VALUES (1, '1', 'Geben Sie die Titel der Bücher an, die im Jahr 2010 erschienen sind und mehr als 300 Seiten haben.', 'π Titel (σ Jahr = 2010 ∧ AnzahlSeiten >300 (Buch))')");
